  }, []);

  return (
    <Template props={props}>
      <Grid
        bgColor="bgColor"
        margin="-10vh 0 0 0"
        addstyle={() => {
          return css`
            position: relative;
            top: 80px;
          `;
        }}
      >
        <CommunityDetailStyle>
          <Grid
            width="350px"
            addstyle={() => {
              return css`
                position: relative;
                margin: 10px auto;
                font-size: 18px;
                font-weight: bold;
                top: 90px;
              `;
            }}
          >
            <Text size="18px" margin="-80px 0 0 0">
              {title}
            </Text>
          </Grid>
          <Grid margin="-570px 0 0 0">
            <CommunityPost/>
            <CommunityPost/>
            <CommunityPost/>

          </Grid>
        </CommunityDetailStyle>
        <Link to="/communitypostwrite">
          <Button is_float="is_float">
            <FontAwesomeIcon icon={faPencilAlt} style={{ width: "20px" }} />
          </Button>
        </Link>
      </Grid>
    </Template>
  );
};

const CommunityDetailStyle = styled.div`
  width: 100%;
  overflow-x: hidden;
  height:90vh;
  /* overflow-y: scroll;
  -ms-overflow-style: none;

  &::-webkit-scrollbar {
    display: none;
  } */
`;

export default CommunityDetail;
