import React, { useState } from "react";
import { Grid, Text, TextArea } from "../elements";
import styled, { css } from "styled-components";
import { flexBox } from "../shared/style";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCamera } from "@fortawesome/free-solid-svg-icons";

const CatDetailInfoWrite = () => {
  const [works, setWorks] = useState([]);
  const changeWorks = (e) => {
    setWorks([...works, e.target.value]);
  };
  console.log(works);
  return (
    <Grid overflow="hidden">
      <Grid margin="50px 0px 25px 0px">
        <Grid
          width="150px"
          height="150px"
          background="#d3d3d3"
          margin="auto"
          addstyle={() => {
            return css`
              ${flexBox("center")}
            `;
          }}
        >
          <FontAwesomeIcon
            icon={faCamera}
            size="3x"
            style={{ color: "white" }}
          />
        </Grid>
      </Grid>
      <Grid>
        <Grid
          display="flex"
          justifyContent="space-around"
          margin="auto"
          width="90%"
        >
          <CheckGrid>
            <CheckBox type="checkbox" value="water" onClick={changeWorks} />
            <CheckText>급수</CheckText>
          </CheckGrid>
          <CheckGrid>
            <CheckBox type="checkbox" value="feed" onClick={changeWorks} />
            <CheckText>사료</CheckText>
          </CheckGrid>
          <CheckGrid>
            <CheckBox type="checkbox" value="treat" onClick={changeWorks} />
            <CheckText>간식</CheckText>
          </CheckGrid>
        </Grid>
        <Grid margin="10px">
          <TextArea
            width="320px"
            type="text"
            placeholder="일기를 입력해주세요. "
            addstyle={() => {
              return css`
                border: 1px solid rgb(${(props) => props.theme.palette.olive});
                border-radius: 10px;
              `;
            }}
          />
          <TextArea
            width="320px"
            type="text"
            placeholder="태그를 입력해주세요. "
            addstyle={() => {
              return css`
                border: 1px solid rgb(${(props) => props.theme.palette.olive});
                border-radius: 10px;
              `;
            }}
          />
        </Grid>
      </Grid>
    </Grid>
  );
};

const CheckGrid = styled.div`
  display: flex;
  justify-content: center;
`;

const CheckText = styled.p`
  font-weight: 800;
  font-size: 16px;
  margin: 0px 5px;
`;

const CheckBox = styled.input`
  width: 20x;
  height: 20px;
`;

export default CatDetailInfoWrite;
