// LIBRARY
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';

// STYLE
import styled, { css } from 'styled-components';

// ELEMENTS
import { Grid, Button, Input, TextArea, Text, Image } from '../elements/index';

// REDUX
import { imgActions } from '../redux/modules/image';

// ROUTE
import { Link } from 'react-router-dom';

// ICON
import { Camera } from "react-feather";



const CommunityPostWrite = () => {
  const [imgBase64, setImgBase64] = useState(""); // 파일 base64
  const [imgFile, setImgFile] = useState(null);	//파일	

  const handleChangeFile = (event) => {
    let reader = new FileReader();

    reader.onloadend = () => {
      // 2. 읽기가 완료되면 아래코드가 실행됩니다.
      const base64 = reader.result;
      if (base64) {
        setImgBase64(base64.toString()); // 파일 base64 상태 업데이트
      }
    }
    if (event.target.files[0]) {
      reader.readAsDataURL(event.target.files[0]); // 파일을 읽어 버퍼에 저장
      setImgFile(event.target.files[0]); // 파일 상태 업데이트
    }
  }



  const dispatch = useDispatch();
  // S3
  const handleInputFile = (e) => {
    const file = e.target.files[0];
    dispatch(imgActions.setInitialState());
    dispatch(imgActions.setFile([file]));
  };

  const Options = [
    {key: 1, value:'게시글 주제를 선택해주세요!'},
    {key: 2, value:'고양이 정보글'},
    {key: 3, value:'평창동 동네 모임'},
    {key: 4, value:'평창동 고양이 용품 나눔'},
  ]
  const [content, setContent] = React.useState('게시글 주제를 선택해주세요!');
  const onChangeHandler=(e)=> {
    setContent(e.currentTarget.value);
  }

  return (
    <Grid 
      bgColor="bgColor" 
      margin="-10vh auto"
      addstyle={() => {
        return css`
          position:relative;
          top:80px;
        `;
      }}
      >
      <CommunityWriteStyle>
      <Grid width="335px" height="auto" margin="0 0 16px 0">
          <Select 
            onChange={onChangeHandler} 
            value={content}
            style={{height:"32px"}}>
          {Options.map((item, index)=>(
              <option key={item.key} value={item.value}>{item.value}</option>
            ))}
          </Select>
        </Grid>
        <Grid width="335px" height="10%">
        <Input 
            placeholder="제목을 입력해주세요."
            width="103%"
            addstyle={() => {
              return css`
                border-radius: 10px;
                margin: 0 0 16px 2px;
              `;
            }}
            />
          <Grid
              margin="0 0 0 12px"
              addstyle={() => {
                return css`
                /* display:flex; */
                white-space:nowrap; 
                overflow-x: auto; 
                height:120px;
                -ms-overflow-style: none;
                &::-webkit-scrollbar { 
                  display: none; 
                }
              `;
            }}
            >

            <Grid
              width={'90px'} 
              height={'90px'} 
              margin={'5.5px'} 
              addstyle={() => {
                return css`
                  position:relative;
                  background:lightgray;
                  display:inline-block;
                  text-align:center;
                  top:-12px;
                  cursor:pointer;
                `;
              }}>
                <Grid
                addstyle={() => {
                  return css`
                    display:flex;
                    flex-direction:column;
                    justify-content:center;
                    align-items:center;
                  `;
                }}>
                <Grid width="95%">
                  <UploadButton htmlFor="input--file">
                    <Camera width="50%" height=" 50%" color="white"/>
                  </UploadButton> 
                </Grid>
                <Upload
                  id="input--file"
                  name="imgFile"
                  type="file"
                  accept="image/png, image/jpeg"
                  style={{ display: 'none' }}
                  // onChange={handleInputFile}
                  onChange={handleChangeFile}
                />
                <div style={{"backgroundColor": "#efefef", "width":"150px", "height" : "150px"}}>
      </div>
                <Text 
                  size="9px" 
                  fontWeight="bold"
                  addstyle={() => {
                    return css`
                      position:relative;
                      top:-12px;
                    `;
                  }}>
                    0/5
                </Text>
                </Grid>
            </Grid>
            <Grid
              width={'90px'} 
              height={'90px'} 
              margin={'5.5px'} 
              addstyle={() => {
                return css`
                  background:lightgray;
                  display:inline-block; 
                `;
              }}><Image width="100%" height="100%"/></Grid>
            <Grid
              width={'90px'} 
              height={'90px'} 
              margin={'5.5px'} 
              addstyle={() => {
                return css`
                  background:lightgray;
                  display:inline-block; 
                `;
              }}><Image width="100%" height="100%"/></Grid>
            <Grid
              width={'90px'} 
              height={'90px'}
              margin={'5.5px'} 
              addstyle={() => {
                return css`
                  background:lightgray;
                  display:inline-block; 
                `;
              }}><Image width="100%" height="100%"/></Grid>
            <Grid
              width={'90px'} 
              height={'90px'}
              margin={'5.5px'} 
              addstyle={() => {
                return css`
                  background:lightgray;
                  display:inline-block;
                `;
              }}><Image width="100%" height="100%"/></Grid>


          </Grid>
          <TextArea 
            placeholder="내용을 입력해주세요." 
            height="221px"
            width="90%"
            addstyle={() => {
              return css`
                resize: none;
                margin: -4px 10px;
              `;
            }}
            />

        </Grid>

        <Grid width="325px">

        </Grid>
        <Grid 
          width="225px"
          height="30px"
          addstyle={() => {
            return css`
            display: flex;
            margin: 60px 0 0 -70px;
            `;
          }}
        >
        <Button 
          width="108px"
          margin="auto"
          fontSize="14px"
          bgColor="D_yellow"
          fontWeight="bold"
          addstyle={() => {
            return css`
            display: flex;
            height: 24px;
            border-radius: 10px;
            align-items:center;
            justify-content: center;
            position:relative;
            top:-65px;
            left:130px;
            `;
          }}>작성하기</Button>
          <Link to='/communitydetail' style={{textDecoration:'none'}}>
          <Button 
          width="108px"
          margin="auto"
          fontSize="14px"
          fontWeight="bold"
          bgColor="D_yellow"
          addstyle={() => {
            return css`
            display: flex;
            height: 24px;
            border-radius: 10px;
            align-items:center;
            justify-content: center;
            position:relative;
            top:-62px;
            left:137px;
            `;
          }}>취소하기</Button>
          </Link>
          </Grid>
      </CommunityWriteStyle>
    </Grid>
  );
};

const CommunityWriteStyle = styled.div`
  /* border: 2px solid rgb(${(props) => props.theme.palette.olive}); */
  width: 350px;
  height: 60vh;
  margin: 10px auto;
  border-radius: 30px;
`;

const Select = styled.select`
  background: rgb(${(props) => props.theme.palette.bgColor});;
  height: 50px;
  border: 1px solid rgb(${(props) => props.theme.palette.olive});
  width: 350px;
  border-radius: 10px;
`;

const Upload = styled.input`
  background-color: white;
  width: 100%;
  border: 2px solid white;
  padding: 20px;
  display: flex;
  justify-content: flex-end;
  align-items: flex-end;
`;

const UploadButton = styled.label`
  position:relative;
  top:16px;
  left:16px;
  width: 120px;
  text-align: center;
  background-color: rgb(${(props) => props.theme.palette.buttonColor});
  color: white;
  cursor: pointer;
  outline: none;
  display: block;
  float: right;
  margin-bottom: 40px;
`;

export default CommunityPostWrite;
