import React from 'react';

// STYLE
import styled, { css } from 'styled-components';

// ELEMENTS
import { Grid, Button, Input, TextArea } from '../elements/index';

const CommunityWrite = () => {
  const Options = [
    {key: 1, value:'게시글 주제를 선택해주세요.'},
    {key: 2, value:'고양이 정보글'},
    {key: 3, value:'동네 모임'},
    {key: 4, value:'고양이 용품 나눔'},
  ]
  const [content, setContent] = React.useState('게시글 주제를 선택해주세요.');
  const onChangeHandler=(e)=> {
    setContent(e.currentTarget.value);
  }

  return (
    <Grid 
      bgColor="bgColor" 
      margin="auto"
      addstyle={() => {
        return css`
          position:relative;
          top:80px;
        `;
      }}
      >
      <CommunityWriteStyle>
        <Grid width="335px" height="10%" margin="0 0 30px 0">
          <Select onChange={onChangeHandler} value={content}>
          {Options.map((item, index)=>(
              <option key={item.key} value={item.value}>{item.value}</option>
            ))}
          </Select>
        </Grid>
        <Grid width="335px" height="10%">
          <Input 
            placeholder="제목을 입력해주세요."
            addstyle={() => {
              return css`
                border-radius: 10px;
              `;
            }}
            />
        </Grid>
        <Grid width="325px">
          <TextArea placeholder="내용을 입력해주세요." height="300px"/>
        </Grid>
        <Grid 
          width="225px"
          height="10px"
          margin="-50px 30px 0 0"
          addstyle={() => {
            return css`
            display: flex;
            `;
          }}
        >
        <Button 
          width="108px"
          margin="auto"
          color="white"
          fontSize="14px"
          bgColor="brown"
          addstyle={() => {
            return css`
            display: flex;
            height: 24px;
            border-radius: 50px;
            align-items:center;
            justify-content: center;
            position:relative;
            top:-80px;
            left:130px;
            `;
          }}>작성하기</Button>
          <Button 
          width="108px"
          margin="auto"
          color="white"
          fontSize="14px"
          bgColor="brown"
          addstyle={() => {
            return css`
            display: flex;
            height: 24px;
            border-radius: 50px;
            align-items:center;
            justify-content: center;
            position:relative;
            top:-80px;
            left:130px;
            `;
          }}>취소하기</Button>
          </Grid>
      </CommunityWriteStyle>
    </Grid>
  );
};

const CommunityWriteStyle = styled.div`
  /* border: 2px solid rgb(${(props) => props.theme.palette.olive}); */
  width: 350px;
  height: 70vh;
  margin: 10px auto;
  border-radius: 30px;
`;

const Select = styled.select`
  height: 50px;
  border: 1px solid rgb(${(props) => props.theme.palette.olive});
  width: 350px;
  border-radius: 10px;
`;

export default CommunityWrite;
