// LIBRARY
import React from "react";
import styled, { css } from "styled-components";

//STYLE
import { flexBox } from "../shared/style";

// ELEMENTS
import { Grid, Text } from "../elements/index";

// ICON
import FavoriteIcon from '@material-ui/icons/Favorite';

const Comment = () => {
  return (
    <Grid
      margin="0px 0px 15px 0px"
      addstyle={() => {
        return css`
          display:flex;
          border-bottom: 1px solid rgb(${(props) => props.theme.palette.olive});
        `;
      }}
    >
      <Grid 
        addstyle={() => {
          return css`
            display:flex;
          `;
      }}>
      <Text margin="2px 3px" fontWeight="700" size="16px">
        댓글
      </Text>
      <Count>25</Count>
      </Grid>

      <Grid 
        addstyle={() => {
          return css`
            display:flex;
          `;
      }}>
      <Grid>
      </Grid>
      <Grid 
        addstyle={() => {
          return css`
            display:flex;
            margin:0 0 0 60px;
          `;
      }}>
      <FavoriteIcon style={{color:'red', position:'relative', bottom:'2px'}}/>
      <Text fontWeight="bold" margin="3px">10</Text>
      </Grid>
      </Grid>

    </Grid>
  );
};

const Count = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 10px;
  background: rgb(${(props) => props.theme.palette.D_yellow});
  font-size: 12px;
  text-align: center;
  line-height: 20px;
`;

export default Comment;
